import React ,{ useState } from  'react'
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import { jsPDF } from "jspdf";


function Quiz() {
  let questions = [
    {
      id: 1,
      question: "HTML stands for -",
      option: [
        "HighText Machine Language",
        "HyperText and links Markup Language",
        "HyperText Markup Language",
        "None of these",
      ],
      correctans: "HyperText Markup Language",
    },
    {
      id: 2,
      question: "The correct sequence of HTML tags for starting a webpage is -",
      option: [
        "Head, Title, HTML, body",
        " HTML, Body, Title, Head",
        "HTML, Head, Title, Body",
        " HTML, Head, Title, HTML",
      ],
      correctans: "HTML, Head, Title, Body",
    },
    {
      id: 3,
      question:
        " Which of the following element is responsible for making the text bold in HTML?",
      option: ["<pre>", "<a>", "<b>", "<br>"],
      correctans: "<b>",
    },
    {
      id: 4,
      question:
        "Which of the following tag is used for inserting the largest heading in HTML?",
      option: ["<h3>", "<h1>", "<h5>", "<h6>"],
      correctans: "<h1>",
    },
    {
      id: 5,
      question:
        " Which of the following tag is used to insert a line-break in HTML?",
      option: ["<br>", "<a>", "<pre> ", "<b>"],
      correctans: "<br>",
    },
  ];
  const onReset = () => {
    setAns({});
  };
  
  const [ans, setAns] = useState({});
  const [countans, setCountans] = useState(0);

  const onChangeClick = (e) => {
    const { name, value } = e.target;
    setAns({ ...ans, [name]: value });
  };

  console.log(ans);

  const handleClick = (e) => {
    e.preventDefault();
    let score=0;
    // props.handleButton();

    questions.map((que)=>{
      console.log(que.correctans, ans[que.id],que.correctans == ans[que.id])
      if(que.correctans == ans[que.id]) {
        console.log('in')
       score++
      }
  })
  setCountans(score)

  localStorage.setItem("result",score)
 
  
};
const downloadReport=()=>{
  let score=localStorage.getItem('result')
  let name=localStorage.getItem('Name')
  const doc = new jsPDF();

doc.text(`YOUR RESULT `, 80, 10);
doc.text(`Name : ${name} `, 10, 20);
doc.text(`Your score : ${score} `, 10, 30);

doc.save(`${name}.pdf`);
  console.log(score)
}
console.log(countans)

  return (  
    <Container fixed>
      <div>
        {questions.map((que) => (
          <div key={que.id}>
            <h2>({que.id}) {que.question}</h2>
            <FormControl component="fieldset">
              <RadioGroup
                name={que.id}
                value={ans[que.id] || ''}
                onChange={onChangeClick}
              >
                {que.option.map((op) => (
                  <FormControlLabel
                    key={op}
                    value={op}
                    control={<Radio />}
                    label={op}
                  />
                ))}
              </RadioGroup>
            </FormControl>
            <hr />
          </div>
        ))}
        <Grid
          container
          direction="row"
          justifyContent="space-evenly"
          alignItems="flex-end"
          style={{marginBottom:"10px"}}
        >
          <Button variant="contained" color="error" onClick={onReset}>
            Reset
          </Button>
          <Button variant="contained" onClick={(e) => { handleClick(e); }}>Submit</Button>
          <Button variant="outlined" size="medium"  onClick={()=>{downloadReport()}}>
            Download Result
          </Button>
        </Grid>
      </div>
    </Container>
  );
}

export default Quiz;
