import React from 'react'
import {  Button,  } from '@mui/material';
import { jsPDF } from "jspdf";
//
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';


const bull = (
    <Box
      component="span"
      sx={{ display: 'inline-block', mx: '2px', transform: 'scale(0.8)' }}
    >
      â€¢
    </Box>
  );

 function Report() {

    const downloadReport = () => {
        const score = localStorage.getItem('result');
        const name = localStorage.getItem('Name');
        const doc = new jsPDF();
      
        doc.setFontSize(18);
        doc.setTextColor("#1976D2"); // Material-UI primary color
      
        doc.text("Quiz Result", 80, 20);
      
        doc.setFontSize(14);
        doc.setTextColor("#333");
      
        doc.text(`Name: ${name}`, 20, 40);
        doc.text(`Your Score: ${score}`, 20, 50);
      
        doc.setLineWidth(0.5);
        doc.line(20, 55, 190, 55); // Horizontal line
      
        doc.setFontSize(10);
        doc.setTextColor("#777");
      
        doc.text(
          "Thank you for participating in the quiz!",
          20,
          75,
          { maxWidth: 170 }
        );
      
        doc.setFontSize(12);
        doc.setTextColor("#1976D2");
      
        doc.text("Downloaded on: " + new Date().toLocaleString(), 20, 100);
      
        doc.save(`${name}_QuizResult.pdf`);
      };
 
  return (
    <div>
         <Card sx={{ minWidth: 275 }}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          Word of the Day
        </Typography>
        <Typography variant="h5" component="div">
          be{bull}nev{bull}o{bull}lent
        </Typography>
        <Typography sx={{ mb: 1.5 }} color="text.secondary">
          adjective
        </Typography>
        <Typography variant="body2">
          well meaning and kindly.
          <br />
          {'"a benevolent smile"'}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Learn More</Button>
      </CardActions>
    </Card>
        
      <Button variant="outlined" size="medium" onClick={downloadReport} style={{marginTop:"10px"}}>
            Download Result
          </Button>
    </div>
  )
}
export default Report;
