import React ,{ useState } from  'react'
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';

 function Quiz() {
  let questions = [
    {
      id: 1,
      question: "HTML stands for -",
      option: [
        "HighText Machine Language",
        "HyperText and links Markup Language",
        "HyperText Markup Language",
        "None of these",
      ],
      correctans: "HyperText Markup Language",
    },
    {
      id: 2,
      question: "The correct sequence of HTML tags for starting a webpage is -",
      option: [
        "Head, Title, HTML, body",
        " HTML, Body, Title, Head",
        "HTML, Head, Title, Body",
        " HTML, Head, Title, HTML",
      ],
      correctans: "HTML, Head, Title, Body",
    },
    {
      id: 3,
      question:
        " Which of the following element is responsible for making the text bold in HTML?",
      option: ["<pre>", "<a>", "<b>", "<br>"],
      correctans: "<b>",
    },
    {
      id: 4,
      question:
        "Which of the following tag is used for inserting the largest heading in HTML?",
      option: ["<h3>", "<h1>", "<h5>", "<h6>"],
      correctans: "<h1>",
    },
    {
      id: 5,
      question:
        " Which of the following tag is used to insert a line-break in HTML?",
      option: ["<br>", "<a>", "<pre> ", "<b>"],
      correctans: "<br>",
    },
  ];

  const [ans, setAns] = useState({});
  const [countans,setCountans]= useState(0)

  const onChangeClick = (e) => {
    console.log(e.target.name, e.target.value);
    const { name, value } = e.target;
    setAns({ ...ans, [name]: value });

  };
  console.log(ans);
  return (  
    <Container  fixed>
    <div>
     
     {questions.map((que)=>(
        <div>
         <h2>({que.id}) {que.question}</h2>
        
        
         {que.option.map((op)=>(
           <div>
             
             <FormControl>
    
      <RadioGroup>
        
        <FormControlLabel value={op} name={que.id} control={<Radio onChange={(e)=>{onChangeClick(e)}} />} label= {op} />
      </RadioGroup>
    </FormControl>
             
            
           </div>  
         ))} <hr />
        </div>
     ))}



<Button variant="contained" color="error">
  Reset
</Button>
        <Button variant="contained">Submit</Button>
        <Button variant="outlined" size="medium">
         Download Reasult
        </Button>

    </div>
    <Grid container spacing={1}>
  <Grid container item spacing={3}>
   df
  </Grid>
  <Grid container item spacing={3}>
    df
  </Grid>
  <Grid container item spacing={3}>
   df
  </Grid>
</Grid>
    </Container>
  )
}
export default Quiz;
